"use strict"
function setServerMetas(){var e={"appx-allow-noLogin":{value:"false"},"appx-allow-specific":{value:"false"},"appx-use-noLogin":{value:"false"},"appx-use-specific":{value:"false"},"appx-auto-user":{value:"{Appx Login Name}"},"appx-auto-pswd":{value:"{Appx Login Password}"},"appx-auto-host":{value:"{Appx Login Server}"},"appx-auto-port":{value:"{Appx Login Port}"},"appx-application":{value:"Appx Application"},"appx-database":{value:"Appx Database"},"appx-procType":{value:"Process Type"},"appx-process":{value:"Name of Process"},"appx-browser-redirect":{value:"Enter Redirect Website Here"}}
for(var a in e){var p=document.createElement("meta")
p.name=a,p.content=e[a].value,document.getElementsByTagName("head")[0].appendChild(p)}}setServerMetas()
