function appxsetfieldhandler(a){setfield(a)}function sendappxdate(a,t,e){var d={cmd:"appxdate",args:[a,t,e],handler:"appxsetfieldhandler",data:null}
appx_session.ws.send(JSON.stringify(d))}function setfield(a){var t=getClientId("appxitem_"+a.data.col.toString()+"_"+a.data.row.toString())
$("#"+t).is("input")?$("#"+t).val(ab2str(a.data.data)):$("#"+t).find(".appxdatevalue").val(ab2str(a.data.data)),$("#"+t).is("input")?$("#"+t).focus():$("#"+t).find(".appxdatevalue").focus(),appx_session.activeDatepicker=null}